#/usr/bin/env python#-*- coding:utf-8 -*-'Wen app framework'# Date: 2017.10.19 __author__ = 'zym'import logging; logging.basicConfig(level=logging.INFO)import asyncio, os, json, timefrom datetime import datetimefrom aiohttp import webdef index(request):	return web.Response(body=b'<h1>Awesome</h1>', content_type='text/html')async def init(loop):	app = web.Application(loop=loop)	app.router.add_route('GET', '/', index)	srv = await loop.create_server(app.make_handler(), '192.168.1.102', 8877)	logging.info('server started at http://192.168.1.102:8877 ... ...')	return srv	loop = asyncio.get_event_loop()loop.run_until_complete(init(loop))loop.run_forever()