# -*- coding：utf-8-*- 'models'import time, uuid #uuid 用于生成Universl User Id## UUID1 利用机器的网络MAC地址和随机数，UUID3利用命名空间和名字，UUID4利用随机数## UUID5 利用命名空间和名字的SHA-1from orm import Model, StringField, BooleanField, FloatField, TextFielddef next_id():	return "%015d%s000" % (int(time.time() * 1000), uuid.uuid4().hex )class User(Model):	__table__ = 'users'		id = StringField(primary_key=True, default=next_id, ddl='varchar(50)')	email = StringField(ddl='varchar(50)')	passwd = StringField(ddl='varchar(50)')	admin = BooleanField()	name = StringField(ddl='varchar(50)')	image = StringField(ddl='varchar(500)')	created_at = FloatField(default=time.time)	class Blog(Model):	__table__ = 'blogs'		id = StringField(primary_key=True, default=next_id, ddl='varchar(50)')	user_id = StringField(ddl='varchar(50)')	user_name = StringField(ddl='varchar(50)')	user_image = StringField(ddl='varchar(500)')	name = StringField(ddl='varchar(50)')	summary = StringField(ddl='varchar(200)')	content = TextField()	created_at = FloatField(default=time.time)class Comment(Model):    __table__ = 'comments'	    id = StringField(primary_key=True, default=next_id, ddl='varchar(50)')    blog_id = StringField(ddl='varchar(50)')    user_id = StringField(ddl='varchar(50)')    user_name = StringField(ddl='varchar(50)')    user_image = StringField(ddl='varchar(500)')    content = TextField()    created_at = FloatField(default=time.time)